select MONTH(START_DATE) as MONTH, CAR_ID, count(HISTORY_ID) as RECORDS from CAR_RENTAL_COMPANY_RENTAL_HISTORY where YEAR(START_DATE) = 2022 and MONTH(START_DATE) between 8 and 10 and CAR_ID in (select CAR_ID from CAR_RENTAL_COMPANY_RENTAL_HISTORY where YEAR(START_DATE) = 2022 and MONTH(START_DATE) between 8 and 10 group by CAR_ID having count(*) >= 5) group by 1, 2 having count(*) > 0 order by 1, 2 desc;